import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";  // Import Link from react-router-dom
import styles from "./styles/Login.module.scss";
import chemiLogo from '../images/chemi.jpeg';  

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);
  const navigate = useNavigate();

  const handleUsername = (e) => {
    setUsername(e.target.value);
  };

  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (username && password) {
      console.log("Login Successful", username, password);
      navigate("/");  // Navigate to the homepage or dashboard
    } else {
      document.getElementById("username-input").classList.add(styles["login-invalid"]);
      document.getElementById("password-input").classList.add(styles["login-invalid"]);
      setErrorMessage("Please enter both username and password.");
    }
  };

  return (
    <div className={styles["login-container"]}>
      <div className={styles["login-logo"]}>
        <a href="/">
          <img src={chemiLogo} alt="Chemi Logo" className={styles["login-logo-image"]} />
        </a>
      </div>
      <span className={styles["login-vertical-divider"]}></span>
      <div className={styles["login-form-container"]}>
        <form onSubmit={handleLogin}>
          <h1>Login</h1>
          <div>
            <input
              id="username-input"
              type="text"
              placeholder="Username"
              onChange={handleUsername}
              className={styles["login-input"]}
            />
          </div>
          <div>
            <input
              id="password-input"
              type="password"
              placeholder="Password"
              onChange={handlePassword}
              className={styles["login-input"]}
            />
          </div>
          {errorMessage && (
            <div className={styles["login-error-message"]}>{errorMessage}</div>
          )}
          <button type="submit" className={styles["login-submit-button"]}>
            Sign In
          </button>
          <div className={styles["login-links"]}>
            <Link to="/register">Don't have an account? Sign up</Link>  // Use Link to navigate to the signup page
            <a href="/">Back to Home</a>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
