import React from 'react';
import { Link } from 'react-router-dom';

const MainMenu = () => {
  const menuBarStyle = {
    display: 'flex',
    flexWrap: 'wrap',  // Allow items to wrap to the next line on smaller screens
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#333',
    color: 'white',
    padding: '0 20px',
    marginTop: '10px',
    height: 'auto'  // Make height auto to accommodate wrapping
  };

  const ulStyle = {
    display: 'flex',
    flexWrap: 'wrap',  // Enable wrapping for the list
    listStyle: 'none',
    padding: '0',
    margin: '0',
    width: '100%'
  };

  const liStyle = {
    padding: '10px 15px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    minWidth: '160px',  // Ensure minimum width to avoid squeezing
    flex: '1 1 auto'  // Allow flex items to grow and shrink but respect min-width
  };

  const linkStyle = {
    textDecoration: 'none',
    color: 'inherit',
    display: 'block'  // Ensure the link fills the li for better clickability
  };

  return (
    <div style={menuBarStyle}>
      <ul style={ulStyle}>
        <li style={liStyle}><Link to="/industries" style={linkStyle}>Industries</Link></li>
        <li style={liStyle}><Link to="/products" style={linkStyle}>Products</Link></li>
        <li style={liStyle}><Link to="/sustainability" style={linkStyle}>Sustainability</Link></li>
        <li style={liStyle}><Link to="/formulations" style={linkStyle}>Formulations</Link></li>
        <li style={liStyle}><Link to="/cosmeticIngredients" style={linkStyle}>Cosmetic Ingredients</Link></li>
        <li style={liStyle}><Link to="/foodIngredients" style={linkStyle}>Food Ingredients</Link></li>
        <li style={liStyle}><Link to="/caseIngredients" style={linkStyle}>CASE Ingredients</Link></li>
        <li style={liStyle}><Link to="/plastics" style={linkStyle}>Plastics</Link></li>
        <li style={liStyle}><Link to="/pharmaceuticals" style={linkStyle}>Pharmaceuticals & Nutraceuticals</Link></li>
      </ul>
    </div>
  );
}

export default MainMenu;
