// src/components/HomePage.js
import React from 'react';
import Header from '../header/Header';
import Footer from '../footer/Footer';
import './HomePage.css';
import backgroundImage from '../../images/chemi.jpeg';  // Adjust the path based on your directory structure

const HomePage = () => {
  const products = [
    { image: '/path/to/image1.jpg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
    // Add more products as needed
  ];

  return (
    <div>
      <Header />
      <div className="background-section">
        <h1>Source, Taste, and Innovate</h1>
      </div>
      <div className="products-container">
        {products.map(product => (
          <ProductCard key={product.name} product={product} />
        ))}
      </div>

    </div>
  );
}

export default HomePage;
