// src/components/HomePage.js
import React from 'react';
import Header from '../header/Header';
import Footer from '../footer/Footer';
import './HomePage.css';
import backgroundImage from '../../images/chemi.jpeg';  // Adjust the path based on your directory structure
import ProductCard from '../ProductCard/ProductCard';

const HomePage = () => {

  const products = [
    { image: '/chemcart/src/images/chemi.jpeg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
    // Add more products as needed, using the same image as the background for uniformity
    { image: '/chemcart/src/images/chemi.jpeg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
    { image: '/chemcart/src/images/chemi.jpeg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
    { image: '/chemcart/src/images/chemi.jpeg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
    { image: '/chemcart/src/images/chemi.jpeg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
  ];
  return ({ image: '/chemcart/src/images/chemi.jpeg', name: 'Advanced Composites ADX-5023', description: 'Thermoplastic Polyolefin (TPO)' },
    <div>
      <Header />
      <div className="background-section" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <h1>Source, Taste, and Innovate</h1>
      </div>
      <main>
      <div className="product-section">
        {products.map(product => (
          <ProductCard key={product.name} product={product} />
        ))}
      </div>
      </main>

    </div>
  );
}

export default HomePage;
